/* eslint-disable import/no-extraneous-dependencies */
import { mount } from 'cypress/react18';

import { BiomeTheme } from './src/types/theme';

declare module '*.svg' {
  const content: any;
  export const ReactComponent: any;
  export default content;
}

declare module '*.webp' {
  const content: any;
  export default content;
}

declare module '@emotion/react' {
  export interface Theme extends BiomeTheme {}
}

declare global {
  namespace Cypress {
    interface Chainable {
      mount: typeof mount;
    }
  }
}

declare global {
  namespace JSX {
    interface IntrinsicElements {
      'hello-world': any;
    }
  }
}
