import { ReactElement } from 'react';

import { useTheme } from '../../hooks';
import { Button } from '../Clickable';
import { shouldInvertColoring } from './styles';
import { BannerRightButtonProps, DEFAULT_BANNER_VARIANT } from './types';

export function BannerRightButton<
  RC extends ReactElement | undefined = undefined,
>({
  variant = 'tertiary',
  bannerVariant = DEFAULT_BANNER_VARIANT,
  ...props
}: BannerRightButtonProps<RC>) {
  const theme = useTheme();
  const invertButtonColoring = shouldInvertColoring(bannerVariant, theme);
  return (
    <Button
      {...props}
      size="small"
      variant={invertButtonColoring ? `${variant}/inverse` : variant}
    />
  );
}

BannerRightButton.displayName = 'Banner.RightButton';
