import merge from 'ts-deepmerge';

import { AspectRatioImage, AspectRatioImageProps } from '../AspectRatioImage';

export type CardAssetImageProps = AspectRatioImageProps<undefined>;

export function CardAssetImage({
  responsiveSizes = [128, 256, 450, 512, 720, 860, 1024],
  sx = {},
  className,
  ...props
}: CardAssetImageProps) {
  return (
    <AspectRatioImage
      {...props}
      rc={<span />}
      className={`${className ?? ''} CardAssetImage`}
      responsiveSizes={responsiveSizes}
      sx={merge(
        {
          bg: 'base.color.neutral.900',
          objectFit: 'cover',
          objectPosition: 'center',
        },
        sx,
      )}
    />
  );
}

CardAssetImage.displayName = 'Card.AssetImage';
