import { ReactElement } from 'react';

import { ButtConProps } from '../../types/clickable';
import { ButtCon } from '../Clickable';

export type CardButtConProps<RC extends ReactElement | undefined = undefined> =
  ButtConProps<RC>;

export function CardButtCon<RC extends ReactElement | undefined = undefined>({
  variant = 'tertiary',
  size = 'small',
  className,
  ...props
}: CardButtConProps<RC>) {
  const { onClick } = 'onClick' in props ? props : { onClick: null };
  return (
    <ButtCon
      {...props}
      size={size}
      variant={variant}
      className={`${className ?? ''} CardButtCon`}
      onClick={ev => {
        ev.stopPropagation();
        if (typeof onClick === 'function') {
          onClick(ev);
        }
      }}
    />
  );
}

CardButtCon.displayName = 'Card.ButtCon';
