import merge from 'ts-deepmerge';

import { CloudImage, CloudImageProps } from '../CloudImage';
import { baseFramedStyles } from './styles';

export type CardFramedLogoProps = CloudImageProps;

export function CardFramedImage({
  sx = {},
  testId,
  responsiveSizes = [32, 64, 128, 256],
  className,
  ...props
}: CardFramedLogoProps) {
  const framedLogoSx = merge(baseFramedStyles, sx);
  return (
    <CloudImage
      {...props}
      responsiveSizes={responsiveSizes}
      testId={testId}
      sx={framedLogoSx}
      className={`${className ?? ''} CardFramedImage`}
    />
  );
}

CardFramedImage.displayName = 'Card.FramedImage';
