import merge from 'ts-deepmerge';

import { Box, BoxProps } from '../Box';
import { Logo, LogoProps } from '../Logo';
import { baseFramedStyles } from './styles';

export type CardFramedLogoProps = BoxProps & Pick<LogoProps, 'logo'>;

export function CardFramedLogo({
  logo,
  sx = {},
  testId,
  className,
  ...props
}: CardFramedLogoProps) {
  const framedLogoSx = merge(baseFramedStyles, sx);
  return (
    <Box
      {...props}
      testId={testId}
      sx={framedLogoSx}
      rc={<span />}
      className={`${className ?? ''} CardFramedLogo`}
    >
      <Logo
        logo={logo}
        sx={merge(
          {
            width: '80%',
          },
          sx,
        )}
      />
    </Box>
  );
}
CardFramedLogo.displayName = 'Card.FramedLogo';
