import merge from 'ts-deepmerge';

import { LinkIconProps } from '../../../types/icon';
import { Icon } from '../../Icon';

export function LinkIcon({
  sx = {},
  variant = 'primary',
  iconVariant,
  ...props
}: LinkIconProps) {
  const allStyles = merge({ fill: `base.color.text.link.${variant}` }, sx);
  return <Icon {...props} sx={allStyles} variant={iconVariant} />;
}

LinkIcon.displayName = 'Link.Icon';
