import merge from 'ts-deepmerge';

import { ModalComponentProps } from '../../types/overlays';
import { Modal } from '../Modal';
import { ModalTarget } from '../Modal/ModalTarget';
import { ConfirmationDialogContent } from './ConfirmationDialogContent';

export type ConfirmationDialogProps = ModalComponentProps & {};

export function ConfirmationDialog({
  bgOverlaySx = {},
  testId = 'ConfirmationDialog',
  ...props
}: ConfirmationDialogProps) {
  return (
    <Modal
      {...props}
      testId={testId}
      ContentSubcomponent={ConfirmationDialogContent}
      bgOverlaySx={merge(
        { bg: 'base.color.translucent.standard.600' },
        bgOverlaySx,
      )}
    />
  );
}

ConfirmationDialog.displayName = 'ConfirmationDialog';
ConfirmationDialog.Target = ModalTarget;
ConfirmationDialog.Content = ConfirmationDialogContent;
