import { ReactElement } from 'react';
import merge from 'ts-deepmerge';

import { ModalContent, ModalContentProps } from '../Modal/ModalContent';

export type DrawerContentProps<
  RC extends ReactElement | undefined = undefined,
> = ModalContentProps<RC> & {};

export function DrawerContent<RC extends ReactElement | undefined = undefined>({
  className,
  sx = {},
  ...props
}: DrawerContentProps<RC>) {
  return (
    <ModalContent
      className={`${className ?? ''} DrawerContent`}
      sx={merge({ minh: 'unset' }, sx)}
      {...props}
    />
  );
}

DrawerContent.displayName = 'Drawer.Content';
