import { IconVariant } from '../../types';
import { DuoConColorVariant } from './DuoCon';

export function getBgFillColoring(
  isDarkMode: boolean,
  colorVariant: DuoConColorVariant,
  variant: IconVariant,
) {
  return isDarkMode && variant === 'regular'
    ? 'base.color.translucent.standard.400'
    : isDarkMode && variant === 'bold'
    ? `base.color.status.${colorVariant}.bright`
    : variant === 'regular'
    ? 'base.color.translucent.standard.800'
    : `base.color.status.${colorVariant}.bright`;
}

export function getFillColoring(
  isDarkMode: boolean,
  colorVariant: DuoConColorVariant,
  variant: IconVariant,
) {
  return isDarkMode && variant === 'regular'
    ? `base.color.status.${colorVariant}.bright`
    : isDarkMode && variant === 'bold'
    ? 'base.color.text.inverse.primary'
    : variant === 'regular'
    ? `base.color.status.${colorVariant}.bright`
    : 'base.color.text.primary';
}
