import { cyGetByTestId, cyMountWithProviders } from '../../utils/testHelpers';
import { Icon } from './Icon';

describe('<Icon icon="loading" />', () => {
  it('should render an animating icon', () => {
    cyMountWithProviders(<Icon icon="Loading" testId="moo" />);
    cyGetByTestId('moo')
      .should('have.css', 'animation')
      .and('include', '1s linear 0s infinite');
  });

  it('should render an icon which is 150px wide', () => {
    cyMountWithProviders(
      <Icon icon="Loading" testId="moo" sx={{ w: '150px' }} />,
    );
    cyGetByTestId('moo').should('have.css', 'width', '150px');
  });
});
