import { MakeResponsive } from '../../types/shared';
import { Body, LoopingText, LoopingTextProps } from '../Text';
import { LoadingOverlaySize } from './LoadingOverlayContent';

export type LoadingOverlayContentLoopingTextProps = LoopingTextProps<
  undefined,
  undefined
> & {
  size?: MakeResponsive<LoadingOverlaySize>;
};

export function LoadingOverlayContentLoopingText({
  size,
  ...props
}: LoadingOverlayContentLoopingTextProps) {
  return <LoopingText {...props} use={<Body size={size} weight="bold" />} />;
}

LoadingOverlayContentLoopingText.displayName =
  'LoadingOverlay.Content.LoopingText';
