import merge from 'ts-deepmerge';

import { Icon, IconProps } from '../Icon';

export type MenuItemIconProps = IconProps;

export function MenuItemIcon({ icon, sx = {}, ...props }: MenuItemIconProps) {
  return (
    <Icon
      {...props}
      icon={icon}
      sx={merge(
        {
          width: 'base.icon.size.300',
          fill: 'base.color.text.primary',
          flexShrink: 0,
        },
        sx,
      )}
    />
  );
}

MenuItemIcon.displayName = 'MenuItem.Icon';
