import merge from 'ts-deepmerge';

import { Icon, IconProps } from '../Icon';

export type MenuItemIntentIconProps = Partial<IconProps>;

export function MenuItemIntentIcon({
  icon = 'ChevronForward',
  sx = {},
  ...props
}: MenuItemIntentIconProps) {
  return (
    <Icon
      {...props}
      icon={icon}
      sx={merge(
        {
          width: 'base.icon.size.200',
          fill: 'base.color.text.secondary',
          flexShrink: 0,
        },
        sx,
      )}
    />
  );
}

MenuItemIntentIcon.displayName = 'MenuItem.IntentIcon';
