import { DistributiveOmit } from '@emotion/react';
import merge from 'ts-deepmerge';

import { OverflowDrawerMenuProps } from '../OverflowMenus';
import { OverflowDrawerMenu } from '../OverflowMenus/OverflowDrawerMenu';

export type MenuItemOverflowDrawerMenuProps = DistributiveOmit<
  OverflowDrawerMenuProps,
  'icon' | 'iconVariant'
>;

export function MenuItemOverflowDrawerMenu({
  children,
  sx = {},
  ...props
}: MenuItemOverflowDrawerMenuProps) {
  return (
    <OverflowDrawerMenu
      {...props}
      size="small"
      sx={merge({ flexShrink: 0 }, sx)}
    >
      {children}
    </OverflowDrawerMenu>
  );
}

MenuItemOverflowDrawerMenu.displayName = 'MenuItem.OverflowDrawerMenu';
