import { HeadingProps } from '../../types/text';

export function isHeading(
  menuItemSize: MenuItemSize,
  props: unknown,
): props is HeadingProps<undefined> {
  return menuItemSize === 'medium';
}

export const DEFAULT_MENU_ITEM_SIZE = 'medium';
export const MENU_ITEM_SIZES = ['xSmall', 'small', 'medium'] as const;
export type MenuItemSize = (typeof MENU_ITEM_SIZES)[number];

export function isSizeAnArray(
  size: unknown,
): size is Array<MenuItemSize | null> {
  return Array.isArray(size);
}
