import { ReactNode } from 'react';

import { ButtConProps } from '../../types/clickable';
import { DrawerComponentProps, DrawerSize } from '../../types/overlays';
import { AllIconKeys } from '../Icon';

export type OverflowMenuCommonProps = Omit<
  ButtConProps<undefined>,
  'rc' | 'icon'
> & {
  children?: ReactNode;
  testId?: string;
  className?: string;
  icon?: AllIconKeys;
};

export type OverflowPopoverMenuProps = OverflowMenuCommonProps;

export type OverflowDrawerMenuProps = OverflowMenuCommonProps &
  Pick<
    DrawerComponentProps,
    | 'showBgOverlay'
    | 'showHeaderBar'
    | 'headerBarTitle'
    | 'outsideClicksClose'
    | 'drawerPosition'
  > & {
    drawerSize?: DrawerSize;
  };
