import { BodySize, BodyWeight } from '@biom3/design-tokens';
import { ReactElement } from 'react';

import { RollingText, RollingTextProps } from '../Text';

export type ProgressBarLeftRollingTextProps<
  RC extends ReactElement | undefined,
  Use extends ReactElement | undefined,
> = RollingTextProps<RC, Use> & {};

export function ProgressBarLeftRollingText<
  RC extends ReactElement | undefined = undefined,
  Use extends ReactElement | undefined = undefined,
>({
  className,
  text,
  rc,
  use,
  ...props
}: ProgressBarLeftRollingTextProps<RC, Use>) {
  const { size } = 'size' in props ? props : { size: 'small' };
  const { weight } = 'weight' in props ? props : { weight: 'regular' };
  return (
    <RollingText
      {...props}
      rc={rc}
      use={use}
      size={size as BodySize}
      weight={weight as BodyWeight}
      text={text}
      sx={{ minw: '50%', '.text': { justifyContent: 'flex-start' } }}
      className={`${className ?? ''} ProgressBarLeftRollingText`}
    />
  );
}

ProgressBarLeftRollingText.displayName = 'ProgressBar.LeftRollingText';
