import { ReactElement } from 'react';

import { BodyProps } from '../../types/text';
import { Body } from '../Text';
import { ProgressBarVariant } from './shared';

export type ProgressBarRightTextProps<RC extends ReactElement | undefined> =
  BodyProps<RC> & {
    variant?: ProgressBarVariant;
    value?: number;
  };

export function ProgressBarRightText<
  RC extends ReactElement | undefined = undefined,
>({ size, className, children, ...props }: ProgressBarRightTextProps<RC>) {
  return (
    <Body
      {...props}
      size={size ?? 'small'}
      sx={{ ml: 'auto' }}
      className={`${className ?? ''} ProgressBarRightText`}
    >
      {children}
    </Body>
  );
}

ProgressBarRightText.displayName = 'ProgressBar.RightText';
