import { ReactElement } from 'react';
import merge from 'ts-deepmerge';

import { FramedImage, FramedImageProps } from '../FramedImage';
import { framedImageSx } from '../TextInput/styles';

export type SelectDefaultFramedImageProps<
  RC extends ReactElement | undefined = undefined,
> = FramedImageProps<RC>;

export function SelectDefaultFramedImage<
  RC extends ReactElement | undefined = undefined,
>({ sx = {}, ...props }: SelectDefaultFramedImageProps<RC>) {
  const allStyles = merge(framedImageSx, sx);
  return <FramedImage {...props} sx={allStyles} />;
}

SelectDefaultFramedImage.displayName = 'Select.DefaultFramedImage';
