import merge from 'ts-deepmerge';

import { IconVariant } from '../../types';
import { Icon, IconProps } from '../Icon';
import { inputIconBaseSx } from '../TextInput/styles';

export type SelectDefaultIconProps = IconProps & {
  iconVariant?: IconVariant;
};
export function SelectDefaultIcon({
  sx = {},
  iconVariant,
  ...props
}: SelectDefaultIconProps) {
  const allStyles = merge(inputIconBaseSx, sx);
  return <Icon {...props} variant={iconVariant} sx={allStyles} />;
}

SelectDefaultIcon.displayName = 'Select.DefaultIcon';
