import { InputTextAlign } from '../../types/input';

export const chevronSx = {
  w: 'base.icon.size.250',
  stroke: 'base.color.brand.1',
  strokeWidth: 2.75,
  fill: 'transparent',
  right: 'base.spacing.x3',
};

export const selectCustomSx = ({
  textAlign,
  disabled,
}: {
  textAlign: InputTextAlign;
  disabled?: boolean;
}) => ({
  position: 'relative',
  display: 'flex',
  alignItems: 'center',
  width: 'auto',
  justifyContent: textAlign === 'left' ? 'flex-start' : 'flex-end',
  paddingRight: textAlign === 'right' ? 'base.spacing.x10' : '0',
  pointerEvents: disabled ? 'none' : 'auto',
});
