import { Dispatch, SetStateAction } from 'react';

import { MakeResponsive } from '../../types/shared';

export const TABLE_SPACING_SIZES = [
  'xxSmall',
  'xSmall',
  'small',
  'medium',
  'xLarge',
  'xxLarge',
] as const;
export type TableSpacing = (typeof TABLE_SPACING_SIZES)[number];
export const DEFAULT_TABLE_SPACING = 'medium';

export type TableSettings = {
  verticalSpacing: MakeResponsive<TableSpacing>;
  horizontalSpacing: MakeResponsive<TableSpacing>;
  rowHoverFx: boolean;
  stripedBg: boolean;
  tableBorder: boolean;
  rowBorder: boolean;
  columnBorder: boolean;
  stickyHeaders: boolean;
  testId: string;
  setThWidths: Dispatch<SetStateAction<number[]>>;
};

export type TableChildParentNode = 'thead' | 'tbody' | 'tfoot';
