import { ReactElement, ReactNode } from 'react';

import { BoxWithRCAndDomProps } from '../Box';

type StringOrNull = string | null;

export type TabsProviderProps = {
  onTabChange?: (value: string) => void;
  currentTabValue?: StringOrNull;
  defaultTabValue?: StringOrNull;
};

export type TabsContextType = {
  currentTabValue: string;
  onTabChange: (value: string) => void;
};

export type TabsProps<RC extends ReactElement | undefined = undefined> = (
  | { currentTabValue: StringOrNull }
  | { defaultTabValue: StringOrNull }
) & { onTabChange?: (value: string) => void } & BoxWithRCAndDomProps<RC>;

export type TabsListProps<RC extends ReactElement | undefined = undefined> =
  BoxWithRCAndDomProps<RC> & {
    contentOrientation?: 'vertical' | 'horizontal';
  };

export type TabsPanelProps<RC extends ReactElement | undefined = undefined> =
  BoxWithRCAndDomProps<RC> & {
    value: string;
    children?: ReactNode;
  };
