import { ReactElement } from 'react';
import merge from 'ts-deepmerge';

import { FramedImage, FramedImageProps } from '../FramedImage';
import { framedImageSx } from './styles';

export type TextInputFramedImageProps<
  RC extends ReactElement | undefined = undefined,
> = FramedImageProps<RC>;

export function TextInputFramedImage<
  RC extends ReactElement | undefined = undefined,
>({ sx = {}, ...props }: TextInputFramedImageProps<RC>) {
  const allStyles = merge(framedImageSx, sx);
  return <FramedImage {...props} sx={allStyles} />;
}

TextInputFramedImage.displayName = 'TextInput.FramedImage';
