import { ReactElement, ReactNode } from 'react';

import { Icon, IconProps } from '../components/Icon/Icon';
import { TextInputSize } from '../components/TextInput/shared';
import { LinkVariant } from './clickable';
import { MakeResponsive, StandardComponentWithProps } from './shared';

export type IconVariant = 'bold' | 'regular';
export type DualVariantIconProps = StandardComponentWithProps<
  SVGSVGElement,
  {
    variant?: IconVariant;
    children?: ReactNode;
  }
>;

export type SingleVariantIconProps = Omit<DualVariantIconProps, 'variant'>;
export type GenericIconProps = DualVariantIconProps | SingleVariantIconProps;

export type IconReactElement = ReactElement<GenericIconProps, typeof Icon>;

export type LinkIconProps = Omit<IconProps, 'variant'> & {
  variant?: LinkVariant;
  iconVariant?: IconVariant;
};

export type TextInputIconProps = IconProps & {
  iconVariant?: IconVariant;
  size?: MakeResponsive<TextInputSize>;
};
