export const IMAGE_SIZE_VARIANTS = [
  'small',
  'medium',
  'large',
  'xLarge',
] as const;
export const DEFAULT_IMAGE_SIZE_VARIANT = IMAGE_SIZE_VARIANTS[1];
export type ImageSizeVariant = (typeof IMAGE_SIZE_VARIANTS)[number];
export type SizeMapping = Record<ImageSizeVariant, number>;
export const TOLERATED_IMAGE_SIZES = [
  32, 64, 128, 256, 370, 450, 512, 640, 720, 860, 1024, 1280, 1440, 1600, 1920,
  2048,
] as const;

export type ResponsiveSizes = Array<(typeof TOLERATED_IMAGE_SIZES)[number]>;
export type ImageComponentKind = 'Avatar' | 'SwapStack' | 'FramedStack';
export type SafeSizes = readonly string[];
