import { ReactNode, Ref } from 'react';

import { DomReferenceRect, MakeResponsive } from './shared';

export const TOOLTIP_SIZES = ['small', 'medium', 'large'] as const;
export const DEFAULT_TOOLTIP_SIZE: TooltipSize = 'medium';
export type TooltipSize = (typeof TOOLTIP_SIZES)[number];

export type TooltipComponentProps = {
  id?: string;
  size?: MakeResponsive<TooltipSize>;
  domRef?: Ref<HTMLButtonElement>;
  children: ReactNode;
};

export type TooltipItem = {
  id: string;
  content: ReactNode;
  targetRect: DomReferenceRect;
  size: MakeResponsive<TooltipSize>;
};

export type TooltipContext = {
  tooltipList: TooltipItem[];
};
