/* eslint-disable import/no-extraneous-dependencies */
import { describe, expect, it } from '@jest/globals';

import { staticMiddleEllipsis } from './ellipsisHelpers';

describe('staticMiddleEllipsis', () => {
  it('should correctly ellipsis text when all params are valid', () => {
    expect(staticMiddleEllipsis({ text: '12345678902342235' })).toEqual(
      '1234...2235',
    );
  });

  it('should correctly control either side of ellipsised text', () => {
    expect(
      staticMiddleEllipsis({ text: '12345678902342235', leftSideLength: 6 }),
    ).toEqual('123456...2235');

    expect(
      staticMiddleEllipsis({ text: '12345678902342235', rightSideLength: 6 }),
    ).toEqual('1234...342235');

    expect(
      staticMiddleEllipsis({
        text: '123456789023542342235',
        rightSideLength: 7,
        leftSideLength: 7,
      }),
    ).toEqual('1234567...2342235');
  });

  it('should simply return the src text string, when ellipsis params are invalid', () => {
    expect(staticMiddleEllipsis({ text: '2235', leftSideLength: 6 })).toEqual(
      '2235',
    );
  });
});
