import { describe, expect, it } from '@jest/globals';

import { capitalize } from './utils';

describe('utils/capitalize()', () => {
  it('should capitalize the first letter of a string, when applicable', () => {
    expect(capitalize('moo')).toEqual('Moo');
    expect(capitalize('Cow')).toEqual('Cow');
    expect(capitalize('12cow')).toEqual('12cow');
    expect(capitalize('$12cow')).toEqual('$12cow');
    expect(capitalize('cow__2')).toEqual('Cow__2');
  });

  it('should return an empty string, when given a falsey value', () => {
    expect(capitalize(undefined)).toEqual('');
    expect(capitalize('')).toEqual('');
  });
});
